mapper.insert:
INSERT INTO 
	MEMBER(home_id, mdn, is_parent, name, photo, school_name, school_grade, school_ban) 
	values(#{home_id}, #{mdn}, #{is_parent}, #{name}, #{photo}, #{school_name}, #{school_grade}, #{school_ban});

mapper.update:
UPDATE MEMBER
mdn=#{mdn}, name=#{name}, photo=#{photo}, school_name=#{school_name}, school_grade=#{school_grade}, school_ban=#{school_ban}
WHERE member_id = #{member_id}

mapper.insert:
INSERT INTO 
	LOCATION(member_id, lat, lng, address) 
	values(#{member_id}, #{lat}, #{lng}, #{address});
